mapper.select:
SELECT * FROM ( SELECT et.idx, et.id, et.type, et.name, et.description, et.image_url, et.bujajae, date_format(et.reg_date, '%Y-%m-%d') as reg_date, et.use_ny, COALESCE(avail_count.cnt, 0) AS avaliable_equipment_cnt, CASE WHEN (bt.key_id IS NOT NULL AND bt.user_id = #{user_id}) THEN '1' ELSE '0' END AS isLike FROM equipment_type_table et LEFT JOIN ( SELECT equipment_type_id, COUNT(*) AS cnt FROM equipment_item_table WHERE status = 'available' GROUP BY equipment_type_id ) avail_count ON et.id = avail_count.equipment_type_id LEFT JOIN bag_table bt ON et.id = bt.key_id
AND bt.user_id = #{user_id}
) AS subquery
use_ny = 'Y'
AND avaliable_equipment_cnt > 0
AND name LIKE CONCAT('%', #{search_text}, '%')
AND type LIKE CONCAT('%', #{type}, '%')
ORDER BY reg_date DESC
LIMIT #{limit_cnt} OFFSET #{startNumber}

SELECT ei.idx, ei.id, ei.equipment_type_id, ei.serial_number, ei.status, date_format(reg_date, '%Y-%m-%d') as reg_date FROM equipment_item_table as ei
equipment_type_id = #{equipment_id}
AND status = 'available'

	select idx, id, type as category, name, description, image_url, bujajae, date_format(reg_date, '%Y-%m-%d') as reg_date from equipment_type_table


            idx = #{idx}


            id = #{id}

