mapper.select:
select idx, title, category, text, date_format(reg_date, '%Y-%m-%d') as reg_date, file_url1, file_url2, file_url3 from board_table



		and title LIKE CONCAT('%', #{search_text}, '%')


		and category = #{type}


order by reg_date desc

	limit #{limit_cnt} OFFSET #{startNumber}

mapper.insert:
INSERT INTO board_table
	(
	 title, category,reg_date, use_at, text, file_url1, file_url2, file_url3
	)
VALUES (
	 #{title}, #{category} ,now(), 'Y', #{text}, #{file_url1}, #{file_url2}, #{file_url3}
	  )

mapper.update:
UPDATE board_table
SET 
	category = #{category},
  title = #{title},
  text = #{text}, 
  reg_date = NOW()

    , file_url1 = #{file_url1}


    , file_url2 = #{file_url2}


    , file_url3 = #{file_url3}

WHERE idx = #{idx}

update board_master
set img_url=NULL
where idx = #{idx}

mapper.select:
SELECT * FROM equipment_item_table


        equipment_type_id = #{equipment_id}


        AND status = 'available'

mapper.insert:
INSERT INTO equipment_type_table
	(id, type, name, description, image_url, bujajae)
VALUES (
	 UUID(), #{category} ,#{name}, #{description}, #{image_url}, #{bujajae}
	  )

mapper.update:
UPDATE equipment_type_table SET type = #{category}, name = #{name}, description = #{description}, bujajae = #{bujajae}, reg_date = NOW()
, image_url = #{image_url}
WHERE idx = #{idx}

mapper.insert:
INSERT INTO equipment_item_table
	(equipment_type_id, id, serial_number)
VALUES (
	 #{equipment_id} , UUID(), #{serial_number}
	  )

INSERT INTO equipment_rental_request
	(id, equipment_id, user_id, rental_start_date, rental_end_date, type, equipment_plan, sa_eob_no, company_nm, position, company_address1, company_address2, file_path)
VALUES (
	 UUID(), #{id}, #{user_id} , #{rental_start_date}, #{rental_end_date}, #{type}, #{equipment_plan}, #{sa_eob_no}, #{company_nm}, #{position}, #{company_address1}, #{company_address2}, #{file_url1} 
	  )

mapper.select:
SELECT err.idx, err.id, err.equipment_id, err.user_id, err.rental_start_date, err.rental_end_date, err.request_status, date_format(err.request_date, '%Y-%m-%d') as request_date, err.type, err.equipment_plan, err.sa_eob_no, err.company_nm, err.position, err.company_address1, err.company_address2, err.file_path, et.name, et.type AS equipment_type, ut.name AS user_name FROM equipment_rental_request err JOIN equipment_item_table eit ON err.equipment_id = eit.id JOIN equipment_type_table et ON eit.equipment_type_id = et.id JOIN user_table ut ON ut.user_id = err.user_id
and err.idx = #{idx}
and err.request_status = #{request_status}
and ut.name LIKE CONCAT('%', #{search_text}, '%')

mapper.update:
update pemtour_table
set pemtour_url = #{pemtour_url},
maintitle = #{maintitle}, title = #{title}, subtitle = #{subtitle}

mapper.insert:
INSERT INTO event_table
	(
	 title, reg_date, use_at, img_url, text, link_url, from_date, to_date
	)
VALUES (
	 #{title}, now(), 'Y', #{img_url}, #{text}, #{link_url}, #{from_date}, #{to_date}
	  )

mapper.update:
update event_table set title =#{title}, reg_date = now(),
img_url = #{img_url},
text =#{text}, link_url = #{link_url}, from_date = #{from_date}, to_date = #{to_date} where idx = #{idx}

update event_table
set img_url=NULL
where idx = #{idx}

mapper.select:
select * from location_table 
where 1=1

	and (
		title LIKE CONCAT('%', #{search_text}, '%') 
	) 


	and location_category = #{location_category}


	order by reg_date desc
	LIMIT #{limit_cnt} OFFSET #{startNumber}

select count(idx) from location_table 
where 1=1

	and location_category = #{location_category}


	and (
		title LIKE CONCAT('%', #{search_text}, '%') 
	)

select * from location_table 
where 1=1
and idx = #{idx}
