mapper.insert:
INSERT INTO user_table
(
	user_id, password, name, mobile, email, birthday, address1, address2, company_nm, company_address1, company_address2, sa_eob_no, position, document_url 
) VALUES (
	#{user_id}, #{password}, #{name}, #{mobile}, #{email}, #{birthday}, #{user_address1}, #{user_address2}, #{company_nm}, #{company_address1}, #{company_address2},
	#{sa_eob_no}, #{position}, #{document_url}
)

mapper.update:
    	UPDATE user_table
    	SET
    		address2 = #{user_address2},
    		company_nm = #{company_nm},
    		company_address1 = #{company_address1},
    		company_address2 = #{company_address2},
    		sa_eob_no = #{sa_eob_no},
    		position = #{position}

		      , name = #{name}


		      , mobile = #{mobile}


		      , email = #{email}


		      , birthday = #{birthday}


		      , address1 = #{user_address1}


		      , document_url = #{document_url}

		    WHERE user_id = #{user_id}

mapper.select:
SELECT err.idx, err.id, err.equipment_id, err.user_id, err.rental_start_date, err.rental_end_date, err.request_status, date_format(err.request_date, '%Y-%m-%d') as request_date, err.type, err.equipment_plan, err.sa_eob_no, err.company_nm, err.position, err.company_address1, err.company_address2, err.file_path, err.reject_msg, et.name, et.type AS equipment_type, ut.name AS user_name FROM equipment_rental_request err JOIN equipment_item_table eit ON err.equipment_id = eit.id JOIN equipment_type_table et ON eit.equipment_type_id = et.id JOIN user_table ut ON ut.user_id = err.user_id WHERE ut.user_id = #{user_id} AND err.request_status != 'cancelled'
and err.request_status = #{request_status}
limit #{limit_cnt} OFFSET #{startNumber}

select count(*) as totalCnt from equipment_rental_request
where user_id = #{user_id}

	and request_status = #{request_status}

mapper.update:
UPDATE equipment_rental_request
SET
	request_status = #{request_status}
WHERE user_id = #{user_id} and id = #{id}

UPDATE equipment_rental_request
SET
	request_status = 'retry',
	file_path = #{file_url}
WHERE user_id = #{user_id} and id = #{id}

mapper.select:
SELECT err.idx, err.id, err.facility_id, err.user_id, err.rental_start_date, err.rental_end_date, err.request_status, date_format(err.request_date, '%Y-%m-%d') as request_date, err.type, err.facility_plan, err.sa_eob_no, err.company_nm, err.position, err.company_address1, err.company_address2, err.file_path, err.reject_msg, et.name, et.type AS facility_type, ut.name AS user_name FROM facility_rental_request err JOIN facility_item_table eit ON err.facility_id = eit.id JOIN facility_type_table et ON eit.facility_type_id = et.id JOIN user_table ut ON ut.user_id = err.user_id WHERE ut.user_id = #{user_id} AND err.request_status != 'cancelled'
and err.request_status = #{request_status}
limit #{limit_cnt} OFFSET #{startNumber}

select count(*) as totalCnt from facility_rental_request
where user_id = #{user_id}

	and request_status = #{request_status}

mapper.update:
UPDATE facility_rental_request
SET
	request_status = #{request_status}
WHERE user_id = #{user_id} and id = #{id}

UPDATE facility_rental_request
SET
	request_status = 'retry',
	file_path = #{file_url}
WHERE user_id = #{user_id} and id = #{id}
